//user의 store 

//초기 state
export const initialState = {

    isLoggedIn : false, 
    user:{
        nickname:'', 
    }, 
};

export const LOG_IN = 'LOG_IN'; 
export const LOG_OUT = 'LOG_OUT'; 

//실제 액션 
export const loginAction = {
    type:LOG_IN,
    data:{
        nickname:'주닐정', 
    }, 
}

export const logOutAction = {
    type:LOG_OUT,
}

const reducer = (state = initialState , action)=>{

    switch(action.type){
        
        case LOG_IN:{
            return {
                ...state,
                isLoggedIn:true,
                user:action.data,
            };
        }
        case LOG_OUT:{
            return {
                ...state,
                isLoggedIn:false,
                user:null,
            }
        }


        default : {
            return {
                ...state,
            }
        }

    }


};



export default reducer;
