import {all,fork,takeLatest, put, delay,call} from 'redux-saga/effects'; 
import axios from 'axios'; //한번 불러온 모듈을 캐싱이 되므로 user.js에서 
                           //axios.defaults.baseURL='http://captainryan.gonetis.com:3065/api'; 해 놓은게 post.js에서도 적용이 된다. 

import { ADD_POST_REQUEST, ADD_POST_SUCCESS, ADD_POST_FAILURE, ADD_COMMENT_REQUEST, ADD_COMMENT_SUCCESS, ADD_COMMENT_FAILURE, LOAD_MAIN_POSTS_REQUEST, LOAD_MAIN_POSTS_SUCCESS, LOAD_MAIN_POSTS_FAILURE, LOAD_HASHTAG_POSTS_SUCCESS, LOAD_HASHTAG_POSTS_FAILURE, LOAD_HASHTAG_POSTS_REQUEST, LOAD_USER_POSTS_FAILURE, LOAD_USER_POSTS_SUCCESS, LOAD_USER_POSTS_REQUEST, LOAD_COMMENTS_REQUEST, LOAD_COMMENTS_SUCCESS, LOAD_COMMENTS_FAILURE } from '../reducers/post';


function addPostAPI(postData){
    console.log('postData===>' , postData); 
    return axios.post('/post',postData,{withCredentials:true}); 
}

function loadMainPostsAPI(){

    return axios.get('/posts'); 
}


function* loadMainPosts(action){

    try{
           const result = yield call(loadMainPostsAPI);       
            console.log('posts==>',result.data); 
            yield put({
                type:LOAD_MAIN_POSTS_SUCCESS,
                data:result.data,

            });

    }catch(e){
        console.error(e); 
        yield put({
            type:LOAD_MAIN_POSTS_FAILURE,
            error: e,
        })
      
    }


}


function loadHashtagPostsAPI(tag){

    return axios.get(`/hashtag/${tag}`); 
}


function* loadHashtagPosts(action){

    try{
           const result = yield call(loadHashtagPostsAPI,action.data);     
           yield put({
               type:LOAD_HASHTAG_POSTS_SUCCESS,
               data: result.data,
           })  
          

    }catch(e){
        console.error(e); 
        yield put({
            type:LOAD_HASHTAG_POSTS_FAILURE,
            error: e,
        })
      
    }


}


function loadUserPostsAPI(id){

    return axios.get(`/user/${id}/posts`); 
}


function* loadUserPosts(action){

    try{
           const result = yield call(loadUserPostsAPI,action.data);    
           yield put({
            type: LOAD_USER_POSTS_SUCCESS,
            data: result.data,
        })  
          
          

    }catch(e){
        console.error(e); 
        yield put({
            type:LOAD_USER_POSTS_FAILURE,
            error: e,
        })
      
    }


}



function* addPost(action){

    try{
            const result = yield call(addPostAPI,action.data);
 
            yield put({
                type:ADD_POST_SUCCESS,
                data:result.data,

            });

    }catch(e){
        console.error('ADD_POST_FAILURE==>' ,e); 
        yield put({
            type:ADD_POST_FAILURE,
            error: e,
        })
      
    }


}



function* whatchAddPost(){
    yield takeLatest(ADD_POST_REQUEST,addPost); 

}

function* watchLoadMainPosts(){
    yield takeLatest(LOAD_MAIN_POSTS_REQUEST,loadMainPosts); 
}






function addCommentAPI(data) {
    return axios.post(`/post/${data.postId}/comment`, { content: data.content }, {
      withCredentials: true,
    });
  }

function* addComment(action){

    try{
        const result =yield call(addCommentAPI,action.data);
        yield put({
            type:ADD_COMMENT_SUCCESS,
            data:{
                postId:action.data.postId,
                comment:result.data,
            },
        });

}catch(e){
    console.log('addComment에러발생==>', e); 
    yield put({
        type:ADD_COMMENT_FAILURE,
        error: e,
    })
  
}


}



function loadCommentstAPI(postId){

    return axios.get(`/post/${postId}/comments`);
}

function* loadComments(action){

    try{
        const result = yield call(loadCommentstAPI,action.data);
        yield put({
            type:LOAD_COMMENTS_SUCCESS,
            data:{
                postId:action.data,
                comments:result.data,
            },
        });

}catch(e){
    console.log(e); 
    yield put({
        type:LOAD_COMMENTS_FAILURE,
        error: e,
    })
  
}


}


function* whatchAddComment(){
    console.log('whatchAddComment'); 
    yield takeLatest(ADD_COMMENT_REQUEST, addComment); 
    
}

function* watchLoadHashtagPosts(){
    console.log('watchLoadHashtagPosts'); 
    yield takeLatest(LOAD_HASHTAG_POSTS_REQUEST,loadHashtagPosts); 
}

function* watchLoadUserPosts(){
    console.log('watchLoadUserPosts');
    yield takeLatest(LOAD_USER_POSTS_REQUEST,loadUserPosts); 
}

function* watchLoadComments(){
    console.log('watchLoadComments'); 
    yield takeLatest(LOAD_COMMENTS_REQUEST,loadComments);
}


export default function* postSaga() {

 yield all([
     fork(watchLoadMainPosts),
     fork(whatchAddPost), 
     fork(whatchAddComment), 
     fork(watchLoadComments),
     fork(watchLoadHashtagPosts),
     fork(watchLoadUserPosts),
 ]); 

}
