import React from 'react'; 
import {Form, Input, Button, Card, Icon, Avatar} from 'antd'; 


const dummy = {
    isLoggedIn : true,  
    mainPosts : [
                   {User : {
                       id:1,
                       nickname:'정준일',
                       content:'첫번째 게시글', 
                   }
                }
    ],
    imagePaths : ['http://zzalbang.kr/wp-content/uploads/2019/06/944afa44ly1g3bon5dh4kj20u0140gvl-851x1024.jpg'],
}


const Home = ()=>{

    return (
            <div>
                {dummy.isLoggedIn && <Form encType="multipart/form-data">               
                    <Input.TextArea maxLength={140} placeholder="어떤일이 있었나요?"/>
                <div>
                    <input type="file" multiple hidden />
                    <Button>이미지 업로드</Button>
                    <Button type="primary" style={{float:'right'}} htmlType="submit">짹짹</Button>
                </div>
                <div>
                {dummy.imagePaths.map((v,i)=>{
                    return (

                        <div key={i} style={{display:'inline-block'}}>
                          <img src={v} style={{width:'200px' }} alt={v} />
                          <div>
                            <Button>제거</Button>
                          </div>
                        </div>
                    )
                })}
                </div>
                </Form>}
                {dummy.mainPosts.map((v,i)=>{
                    return (
                        <Card
                            key={i}
                            cover={v.img && <img alt="example" src={v.img} />}
                            actions={[
                                <Icon type="retweet" key="retweet"/>,
                                <Icon type="heart" key="heart"/>,
                                <Icon type="ellipsis" key="ellipsis"/>,
                            ]}
                            extra={<Button>팔로우</Button>}
                        >
                        <Card.Meta 
                            avatar={<Avatar>{v.User.nickname[0]}</Avatar>}
                            title={v.User.nickname}
                            description={v.content}
                        />
                        </Card>
                    )
                })}
            </div>
        )

}

export default Home;
