import React , {useEffect,useCallback} from 'react'
import { Button , List,  Card} from 'antd'
import {StopOutlined 
} from '@ant-design/icons';
import {useDispatch ,useSelector} from 'react-redux'
import NicknameEditForm from '../components/NicknameEditForm'
import { LOAD_FOLLOWERS_REQUEST, LOAD_FOLLOWINGS_REQUEST, UNFOLLOW_USER_REQUEST, FOLLOW_USER_REQUEST, REMOVE_FOLLOWER_REQUEST } from '../reducers/user';
import { LOAD_USER_POSTS_REQUEST } from '../reducers/post';
import PostCard from '../components/PostCard'; 

const Profile = ()=>{



    const dispactch  = useDispatch(); 
    const {me ,followerList, followingList } = useSelector(state=>state.user); 
    const {mainPosts} = useSelector(state=>state.post); 
    
    useEffect(()=>{

        dispactch({
            type: LOAD_FOLLOWERS_REQUEST, 
            data: me.id, 
        }); 


        dispactch({
            type: LOAD_FOLLOWINGS_REQUEST, 
            data: me.id, 
        });


        dispactch({
            type: LOAD_USER_POSTS_REQUEST, 
            data: me.id, 
        });


    },[]); 


    const onUnfollow = useCallback(userId=>()=>{
        dispactch({
            type:UNFOLLOW_USER_REQUEST, 
            data: userId, 
        })

    },[])

    const onRemoveFollower =useCallback(userId=>()=>{
        dispactch({
            type:REMOVE_FOLLOWER_REQUEST, 
            data: userId, 
        })

    },[])


    return (
       
        <div>
          
                <NicknameEditForm />
                <List 
                    style={{marginBottom:'20PX'}}
                    grid={{gutter:4 , xs : 2, md : 3}}
                    size="small"
                    header={<div>팔로잉 목록</div>}
                    loadMore={<Button style={{width:'100%'}}>더 보기</Button>}
                    bordered
                    dataSource={followingList}
                    renderItem={item=>(
                        <List.Item style={{marginTop:'20px'}}>
                            <Card key='stop' actions={[<StopOutlined onClick={onUnfollow(item.id)}/>]}>
                                <Card.Meta description={item.nickname}/></Card>
                        </List.Item>
                    )}
                />
                 <List 
                    style={{marginBottom:'20PX'}}
                    grid={{gutter:4 , xs : 2, md : 3}}
                    size="small"
                    header={<div>팔로워 목록</div>}
                    loadMore={<Button style={{width:'100%'}}>더 보기</Button>}
                    bordered
                    dataSource={followerList}
                    renderItem={item=>(
                        <List.Item style={{marginTop:'20px'}}>
                            <Card key='stop' actions={[<StopOutlined onClick={onRemoveFollower(item.id)}/>]}>
                                <Card.Meta description={item.nickname}/></Card>
                        </List.Item>
                    )}
                />
                <div>
                    {mainPosts.map((v,i)=>(
                        <PostCard key={i} post={v} />
                    ))}
                </div>

        </div>
    )
}

export default  Profile;
