export const REMOVE_FOLLOWER_FAILURE = 'REMOVE_FOLLOWER_FAILURE';

export const ADD_POST_TO_ME = 'ADD_POST_TO_ME'

const reducer = (state = initialState , action)=>{

    switch(action.type){
        
        case LOG_IN_REQUEST:{
            return {
                ...state,
                isLoggingIn:true,
               logInErrorReason:'',
            };
        }
        
        case LOG_IN_SUCCESS:{
            console.log('action data ==>' , action.data); 
            return {
                ...state,
                isLoggingIn:false,
                isLoggedIn:true,
                me:action.data,
                isLoading:false,
                successMesage :'로그인에 성공하였습니다.', 


            }
        }
        case LOG_IN_FAILURE: {
            return{
                ...state,
                isLoggingIn:false,
                isLoggedIn: false, 
                logInErrorReason:action.error,
                me:null,
            }    
        }

        case LOG_OUT_REQUEST:{
            return {
                ...state,
                isLoggedIn:false,
                me:null,
            }
        }
        case SIGN_UP_REQUEST:{
            return{
                ...state,
                isSigningUp:true,
                isSignedUp:false,
                signUpErrorReason:'', 
            }
        }

        case SIGN_UP_SUCCESS:{
            return{
                ...state,
                isSigningUp:false,
                isSignedUp: true,
            }
        }
        case SIGN_UP_FAILURE:{
            return{
                ...state,
                isSigningUp:false,
                signUpErrorReason: action.error,
            }
        }



        default : {
            return {
                ...state,
            }
        }

    }


};



export default reducer;
