}



export default function* userSaga() {

    yield all([
        fork(watchLogin), 
        fork(watchLogout),
        fork(watchLoadUser), 
        //fork(watchHello),
        fork(watchSignUp),
        fork(watchFollowUser), 
        fork(watchUnFollowUser), 
        fork(watchLoadFollowers), 
        fork(watchLoadFollowings), 
        fork(watchRemoveFollower), 
        
    ]);

}
